import groovy.json.JsonSlurper
responseContent = testRunner.testCase.getTestStepByName("Read User New List").getPropertyValue("response")
slurperresponse = new JsonSlurper().parseText(responseContent)

for (item in slurperresponse){
	if ( item['username'] == 'testuser'){
		testRunner.testCase.getTestStepByName("User").setPropertyValue('id', item['id'].toString() );
	}
}

